class product {
  String name;
  int size;
  int price;

  product(String str, int num, int menuPrice) {
    name = str;
    size = num;
    price = menuPrice;
  }

  public int totalPrice() {
    return price * size;
  }

  public int showPrice() {
    return price;
  }

  public void init() {
    size = 0;
  }

  public String menuName() {
    return name;
  }

  public int menuNum() {
    return size;
  }

  public void addMenu(int a) {
    size += a;
  }
}
